  janitor::clean_names() %>% 
  dplyr::mutate_all(funs(gsub(",", "", .)))



mydata <- mydata %>% 
  tidyr::separate("x0", c("zeroY", "zeroN")) %>%
  tidyr::separate("x1", c("oneY", "oneN")) %>%
  tidyr::separate("x2", c("twoY", "twoN")) %>%
  tidyr::separate("x3", c("threeY", "threeN")) %>%
  tidyr::separate("x4", c("fourY", "fourN")) %>%
  tidyr::separate("x5", c("fiveY", "fiveN")) %>%
  tidyr::separate("x6", c("sixY", "sixN")) %>% 
  tidyr::separate("x7", c("sevenY", "sevenN")) %>% 
  tidyr::separate("x8", c("eightY", "eightN")) %>% 
  tidyr::separate("x9", c("nineY", "nineN"))


mydata <- mydata %>% 
  dplyr::mutate(pt0 = round(100*as.numeric(zeroY)/as.numeric(zeroN),0)) %>% 
  dplyr::mutate(pt1 = round(100*as.numeric(oneY)/as.numeric(oneN),0)) %>% 
  dplyr::mutate(pt2 = round(100*as.numeric(twoY)/as.numeric(twoN),0)) %>% 
  dplyr::mutate(pt3 = round(100*as.numeric(threeY)/as.numeric(threeN),0)) %>% 
  dplyr::mutate(pt4 = round(100*as.numeric(fourY)/as.numeric(fourN),0)) %>% 
