  dplyr::mutate(pt8 = round(100*as.numeric(eightY)/as.numeric(eightN),0)) %>% 
  dplyr::mutate(pt9 = round(100*as.numeric(nineY)/as.numeric(nineN),0))

mydata <- mydata %>% 
  dplyr::select(c(1,2,3,4,5,6,28:38 ))

mydata <- mydata %>% mutate_each(funs(replace(., is.nan(.), 0)))

